import React, {useState} from 'react';
import Contact from '../pure/contact';
import { User } from '../../models/userClass';


const ContactList = () => {

    const userList = [
        new User('Pedro','Rodriguez',false),
        new User('Carlos','Perez',true),
        new User('Ana','López',false),
    ];
    
    const [list, setList] = useState(userList);

    const changeStatus = (usuario) =>{
        const index = list.indexOf(usuario);
        // const tempUserList = [...list];
        // tempUserList[index].status = !tempUserList[index].status;   
        // setList(tempUserList);
        console.log("empezando a modificar lista",list);
        list[index].status = !list[index].status;   
        console.log("lista modificada",list);
        setList(list)
    }

    return (

            <table >
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>Status</th>
                        <th className='btn-change-status'></th>
                    </tr>
                </thead>
                <tbody>
                    {/* <tr>
                        <td>carmen</td>
                        <td>lopez</td>
                        <td>conectada</td>
                        <td><i class="bi bi-toggle2-off"></i></td>
                    </tr> */}
                    {
                        list.map((elemento,index)=><Contact key={index} usuario={elemento} changeStatus={changeStatus}/>)
                    }
                        {/* <Contact usuario={userList[0]}/>
                        <Contact usuario={userList[1]}/>
                        <Contact usuario={userList[2]}/> */}

                        
                </tbody>
            </table>
    );
};


export default ContactList;
