import React, { useState, useRef } from 'react';
import Contact from '../pure/contact';
import { User } from '../../models/userClass';
import '../../css/contactList.css'


const ContactList = () => {

    const userList = [
        new User('Pedro','Rodriguez',false),
        new User('Carlos','Perez',true),
        new User('Ana','López',false),
    ];
    
    const [list, setList] = useState(userList);
    const [show, setShow] = useState(true);

    const miRef = useRef('');


    const changeStatus = (usuario) =>{
        const index = list.indexOf(usuario);
        const tempUserList = [...list];
        tempUserList[index].status = !tempUserList[index].status;   
        setList(tempUserList);
    }

    const removeContact = (usuario) =>{
        console.log("quitando usuario");
        const index = list.indexOf(usuario);
        const tempUserList = [...list];
        tempUserList.splice(index,1);
        setList(tempUserList);
    }
    
    // const createContact = () =>{

        
    // }

    const showContact =(usuario)=>{
        console.log("mostrando datos de: ", usuario)
    }

    return (
            <table>
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>Status</th>
                        <th className='btn-change-status'></th>
                        <th className='remove'></th>
                    </tr>
                </thead>
                <tbody>
                    {list.map((elemento,index)=>
                        <Contact 
                        key={index} 
                        usuario={elemento} 
                        changeStatus={changeStatus}
                        removeContact={removeContact}
                        showContact={showContact}
                        />
                        )
                    }
                </tbody>
            </table>
    );
};


export default ContactList;
